mapper.select:
SELECT * FROM member
id LIKE '%${sval}%' OR member.no IN (SELECT no FROM member.name WHERE name LIKE '%${sval}%')
ORDER BY no *1 DESC LIMIT #{startRow}, #{size}

mapper.update:
UPDATE member SET
	banMem = '정상'
WHERE no=#{no}

UPDATE member SET
	banMem = '휴면'
WHERE no=#{no}

UPDATE member SET id = #{id}, password = #{password}, 
name = #{name}, tel = #{tel}, email = #{email}, addr = #{addr}, birth = #{birth}, joinDate = #{joinDate},
lastVisit = #{lastVisit}, banMem = #{banMem} WHERE no = #{no}

UPDATE member SET
	lastVisit=#{lastVisit}
WHERE id=#{id}
