mapper.select:
SELECT * FROM member
id LIKE '%${sval}%' OR member.no IN (SELECT no FROM member.name WHERE name LIKE '%${sval}%')
ORDER BY no *1 DESC LIMIT #{startRow}, #{size}

mapper.update:
UPDATE member SET
	lastVisit=NOW()
WHERE id=#{id}

UPDATE member SET
	password=#{password}
WHERE id=#{id}
